};

// Calculate available minutes in working day (8 hours = 480 minutes)
const availableMinutes = 480;

// Generate schedule with time slots
const schedule = [];
let currentTime = new Date();
currentTime.setHours(9, 0, 0, 0); // Start at 9 AM

// Process each todo and assign a time slot
todos.forEach((todo, index) => {
    // Skip if we're past working hours (4:15 PM is latest we can start a task)
    if (currentTime.getHours() >= 16 && currentTime.getMinutes() >= 15) {
        return;
    }

    // Format start time
    const startHour = currentTime.getHours().toString().padStart(2, '0');
    const startMinute = currentTime.getMinutes().toString().padStart(2, '0');
    const startTime = `${startHour}:${startMinute}`;

    // Calculate end time
    const endTime = new Date(currentTime.getTime() + todo.duration_minutes * 60000);
    const endHour = endTime.getHours().toString().padStart(2, '0');
    const endMinute = endTime.getMinutes().toString().padStart(2, '0');

    // Add to schedule
    schedule.push({
        todo_id: todo.todo_id || todo._id || todo.id,
        description: todo.description,
        priority: todo.priority,
        start_time: startTime,
        end_time: `${endHour}:${endMinute}`,
        duration_minutes: todo.duration_minutes,
        notes: index === 0 ? "Highest priority task for today" : null
    });

    // Move time forward for next task (add task duration + 15 min break)
    currentTime = new Date(endTime.getTime() + 15 * 60000); // Add 15-minute break
});

// Calculate total scheduled minutes
const totalScheduledMinutes = schedule.reduce((total, task) => total + task.duration_minutes, 0);

// Calculate utilization percentage
